<style>
@keyframes lamp {
    from {
      fill: #f9b233;
    }

    to {
      fill: #ffff1f;
    }
}

@keyframes move-left {
  to {
    transform: translateX(-5vw);
  }
}

.motorbike {
  width: 28vw;
  position: absolute;
  bottom: 4.5rem; /* 80% * road */
  right: -3vw;
  animation: move-left 240s linear forwards;
  z-index: 6;
}

.cls-1 {
  fill: #c0efec;
}

.cls-2 {
  fill: #192e2e;
}

.cls-3 {
  fill: #548080;
}

.cls-4 {
  fill: azure;
}

.cls-5 {
  fill: #daecec;
}

.cls-6 {
  fill: #98c93f;
}

.cls-7 {
  fill: #daf9f9;
}

.cls-8 {
  fill: #468689;
}

.cls-9 {
  fill: #ffd9b3;
}

.cls-10 {
  fill: #f9b233;
}

.cls-11 {
  fill: #81b8be;
}

.cls-12 {
  fill: #3c3c3b;
}

.cls-13 {
  fill: #ffe9ca;
}

.lamp {
  animation: lamp 3s linear infinite alternate;
}

.person-and-bike {
  animation: vehicle 1s linear infinite;
}

.leaf-1 {
  animation: fadeout 2s infinite, leaf-1 2s infinite;
}

.leaf-2 {
  animation: fadeout 1.5s infinite, leaf-2 1.5s infinite;
}

.leaf-3 {
  animation: fadeout 2.5s infinite, leaf-3 2.5s infinite;
}
</style>